svg.script:
var objList = [
    {id: "path3095", frame: 0},
    {id: "path3097", frame: 1},
    {id: "path3099", frame: 2}
];

function onDisplayReady() {
    onFrameChange(sozi.location.getFrameIndex());
}

function onFrameChange(index) {
    for (var i = 0; i < objList.length; i ++) {
        document.getElementById(objList[i].id).style.visibility =
            objList[i].frame === index ? "visible" : "hidden";
    }
}

sozi.events.listen("sozi.display.ready", onDisplayReady);
sozi.events.listen("sozi.player.framechange", onFrameChange);
